<?phpdeclare(strict_types=1);namespace App\Auth\Command\SignIn;use App\Auth\Entity\User\Email;use App\Auth\Entity\User\UserRepository;use App\Auth\Service\PasswordHasher;use App\Auth\Service\Tokenizer;use App\Flusher;use App\Security\SessionManager;class Handler{    private UserRepository $users;    private Flusher $flusher;    private Tokenizer $tokenizer;    private PasswordHasher $hasher;    public function __construct(UserRepository $users, Flusher $flusher, Tokenizer $tokenizer, PasswordHasher $hasher)    {        $this->users = $users;        $this->flusher = $flusher;        $this->tokenizer = $tokenizer;        $this->hasher = $hasher;    }    public function handle(Command $command): void    {        $email = new Email($command->email);        $user = $this->users->getByEmail($email);        $id = $user->getId();        $user->passwordValidate($command->password, $this->hasher);    }}