<?phpdeclare(strict_types=1);namespace App\Http\Action\v1\Military\AddUnits;use App\Http\EmptyResponse;use App\Http\Validator\Validator;use App\Military\Command\AddUnits\Command;use App\Military\Command\AddUnits\Handler;use Psr\Http\Message\ResponseInterface;use Psr\Http\Message\ServerRequestInterface;use Psr\Http\Server\RequestHandlerInterface;class AddUnitsAction implements RequestHandlerInterface{    private Handler $handler;    public function __construct(Handler $handler)    {        $this->handler = $handler;    }    /**     * @inheritDoc     */    public function handle(ServerRequestInterface $request): ResponseInterface    {        $data = $request->getParsedBody();        $command = new Command();        $command->name = $data['name'] ?? '';        $command->address = $data['address'] ?? '';        $command->firstname = $data['firstname'] ?? '';        $command->lastname = $data['lastname'] ?? '';        $command->surname = $data['surname'] ?? '';        $command->amount = $data['amount'] ?? 0; //TODO added nullable        $command->officials = $data['officials'] ?? null;        $this->handler->handle($command);        return new EmptyResponse(200);    }}