<?phpdeclare(strict_types=1);namespace App\Military\Entity\Units;use Doctrine\Common\Collections\ArrayCollection;use Doctrine\Common\Collections\Collection;use Doctrine\ORM\Mapping as ORM;use DomainException;/** * @ORM\Entity * @ORM\Table(name="military_units") */class Units{    /**     * @ORM\Column(type="military_units_id")     * @ORM\Id     */    private Id $id;    /**     * @ORM\Column(type="military_units_name")     */    private Name $name;    /**     * @ORM\Column(type="military_units_address")     */    private Address $address;    /**     * @ORM\Embedded(class="Commander")     */    private Commander $commander;    /**     * @ORM\Column(type="integer")     */    private string $amount;    /**     * @ORM\OneToMany(targetEntity="UnitsOfficials", mappedBy="units", cascade={"all"}, orphanRemoval=true)     */    private ?Collection $officials;    public function __construct(        Id $id,        Name $name,        Address $address,        Commander $commander,        string $amount    )    {        $this->id = $id;        $this->name = $name;        $this->address = $address;        $this->commander = $commander;        $this->amount = $amount;        $this->officials = new ArrayCollection();    }    public function attachOfficials(Officials $official): void    {        /** @var UnitsOfficials $existing */        foreach ($this->officials as $existing) {            if ($existing->getOfficials()->isEqualTo($official)) {                throw new DomainException('Official is already attached.');            }        }        $this->officials->add(new UnitsOfficials($this, $official));    }    /**     * @return Id     */    public function getId(): Id    {        return $this->id;    }    /**     * @return Name     */    public function getName(): Name    {        return $this->name;    }    /**     * @return Address     */    public function getAddress(): Address    {        return $this->address;    }    /**     * @return Commander     */    public function getCommander(): Commander    {        return $this->commander;    }    /**     * @return string     */    public function getAmount(): string    {        return $this->amount;    }    /**     * @return ArrayCollection|Collection|null     */    public function getOfficials()    {        return $this->officials;    }}