<?phpdeclare(strict_types=1);namespace App\Military\Entity\Units;use Doctrine\ORM\EntityManagerInterface;use Doctrine\ORM\EntityRepository;class UnitsRepository{    private EntityManagerInterface $em;    private EntityRepository $repo;    public function __construct(EntityManagerInterface $em, EntityRepository $repo)    {        $this->repo = $repo;        $this->em = $em;    }    public function add(Units $units): void    {        $this->em->persist($units);    }}