<?phpdeclare(strict_types=1);namespace App\Military\Query\AllUnits;use App\Military\Entity\Units\UnitsRepository;class Handler{    private UnitsRepository $units;    public function __construct(UnitsRepository $units)    {        $this->units = $units;    }    public function handle(Command $command): array    {        //if(//TODO ждет реализации TokenAccessRepository для проверки переданного токена доступа)        $units = $this->units->getAllUnits();        foreach ($units as $unit) {            return array_map(function ($input): string            {                if (is_object($input)){                    return (string)$input;                }                return $input;            }, $unit);        }        return $units;    }}