<?phpnamespace Test\Functional;use function json_encode;class Json{    /**     * @param string $data     * @return array     */    public static function decode(string $data): array    {        /** @var array */        return json_decode($data, true, 512, JSON_THROW_ON_ERROR);    }    public static function encode(string $data): string    {        return json_encode($data, JSON_THROW_ON_ERROR);    }}